import React, { useState } from 'react';
import "./StartPage.css";

const StartPage = () => {
  const [name, setName] = useState('');
  const [handle, setHandle] = useState('');

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleHandleChange = (event) => {
    setHandle(event.target.value);
  };

  const handleStartClick = () => {
    if (name && handle) {
    //   onStart({ name, handle });
      console.log("Handle submitted")
    } else {
      alert("You need to enter a handle before starting the game");
    }
  };

  return (
    <div className="start-container">
      <h1 className="start-title">欢迎来到你的网页游戏</h1>
      <div className="input-container">
        <label className="input-label">
          姓名:
          <input type="text" value={name} onChange={handleNameChange} className="input-field" />
        </label>
        <label className="input-label">
          昵称:
          <input type="text" value={handle} onChange={handleHandleChange} className="input-field" />
        </label>
      </div>
      <button onClick={handleStartClick} className="start-button">
        开始游戏
      </button>
    </div>
  );
};

const styles = {
  container: {
    textAlign: 'center',
    marginTop: '100px',
  },
  title: {
    fontSize: '24px',
    marginBottom: '20px',
  },
  inputContainer: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    marginBottom: '20px',
  },
  label: {
    marginBottom: '10px',
  },
  input: {
    padding: '8px',
    fontSize: '16px',
  },
  button: {
    padding: '10px 20px',
    fontSize: '18px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
};

export default StartPage;
