function CheckExacts(generateColors, guessColors) {
    let i = 0;
    let exact = 0;
    while (i < 4) {
        //When a match is made, cross out the matched elements in both guess and secret.
        if (generateColors[i] === guessColors[i]) {
            exact = exact + 1;
            generateColors[i] = "white";
            guessColors[i] = "black";
        }
        i++;
    }
    return exact;
}

function CheckPartials(generateColors, guessColors) {
    let i = 0;
    let partial = 0;
    while (i < 4) {
        let j = 0;
        while (j < 4) {
        //When a match is made, cross out the matched elements in both guess and secret.
            if (generateColors[i] === guessColors[j]) {
                partial = partial + 1;
                generateColors.setCharAt(i,'-');
                guessColors.setCharAt(j,'*');
            }
        j++;
        }
            i++;
        }
    return partial;
}

function CheckMatches(generateColors, guessColors) {
    const exact = CheckExacts(generateColors, guessColors);
    const partial = CheckPartials(generateColors, guessColors);
    return [exact, partial];
}

export default CheckMatches;
