import React, { useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowLeft } from "@fortawesome/free-solid-svg-icons";
import { Container, Row, Col } from "react-bootstrap";
import "./GameRecord.css"; // Import your custom CSS file for styling

function GameRecord({ onGamePageChange }) {
  const [selectedComponent, setSelectedComponent] = useState("userScores");

  // Sample data for demonstration purposes
  const userScores = [
    { score: 120, date: "2023-01-15" },
    { score: 90, date: "2023-01-10" },
  ];
  const highScores = [
    { userid: "123", username: "JohnDoe", score: 150, date: "2023-01-15" },
    { userid: "456", username: "David", score: 140, date: "2023-02-12" },
  ];

  return (
    <Container>
      <div className="back-container" onClick={onGamePageChange}>
        <FontAwesomeIcon icon={faArrowLeft} size="2x" />
        <p>Game Record List is coming soon</p>
      </div>
      <div className="select-container">
        {/* Dropdown to select the component */}
        <select
          className="form-select"
          value={selectedComponent}
          onChange={(e) => setSelectedComponent(e.target.value)}
        >
          <option value="userScores">Your Own Scores</option>
          <option value="highScores">All High Scores</option>
        </select>
      </div>
      {/* UserScores component */}
      {selectedComponent === "userScores" && (
        <div className="score-container">
          <h2>Your Previous Game Scores</h2>
          <Row>
            <Col>
              <strong>Score</strong>
            </Col>
            <Col>
              <strong>Date</strong>
            </Col>
          </Row>
          {userScores.map((score) => (
            <Row key={score.date}>
              <Col>{score.score}</Col> <Col>{score.date}</Col>
            </Row>
          ))}
        </div>
      )}
      {/* HighScores component */}
      {selectedComponent === "highScores" && (
        <div className="score-container">
          <h2>High Scores</h2>
          <Row>
            <Col>
              <strong>UserID</strong>
            </Col>{" "}
            <Col>
              <strong>Username</strong>
            </Col>
            <Col>
              <strong>Score</strong>
            </Col>{" "}
            <Col>
              <strong>Date</strong>
            </Col>
          </Row>
          {highScores.map((highScore) => (
            <Row key={highScore.userid}>
              <Col>{highScore.userid}</Col> <Col>{highScore.username}</Col>{" "}
              <Col>{highScore.score}</Col>
              <Col>{highScore.date}</Col>
            </Row>
          ))}
        </div>
      )}
    </Container>
  );
}

export default GameRecord;
