import { useEffect, useState } from "react";
import CheckMatches from "./CheckMatches";
import { Button, Image } from "react-bootstrap";
import "./App.css";

function MastermindHeader() {
  return (
    <div className="mastermind-header">
      <h1>Mastermind Game</h1>
      <p>Guess the secret colors!</p>
    </div>
  );
}

function Instruction() {
  const instructions = [
    "In this game, you'll have to guess colors correctly.",
    "The secret code consists of a combination of 4 colors, and your task is to figure out the correct order.",
    "You only have 10 attempts. Try to solve it within the given attempts!",
    "After each guess, you'll receive feedback. Use this feedback to refine your guesses and crack the code!",
  ];

  return (
    <div className="instruction">
      <h2>Instructions</h2>
      <ul>
        {instructions.map((instruction, index) => (
          <li key={index}>{instruction}</li>
        ))}
      </ul>
    </div>
  );
}

function Circle({ color, onCircleClick }) {
  return (
    <button
      className="circle"
      onClick={onCircleClick}
      style={{ backgroundColor: color }}
    ></button>
  );
}

function OneGuessBoard({
  selectColor,
  isActive,
  phrase,
  onRowSubmit,
  isReset,
  onWin,
}) {
  const [colors, setColors] = useState(Array(4).fill("#a3a3a3"));

  function handleClick(i) {
    const newColors = colors.slice();
    newColors[i] = selectColor;
    {
      isActive() && setColors(newColors);
    }
  }

  const [match, setMatch] = useState([null, null]);

  function handleSubmitClick() {
    if (colors.every((color) => color !== "#a3a3a3")) {
      const [exact, partial] = CheckMatches(phrase, colors);
      setMatch([exact, partial]);
      if (exact == 4) {
        onWin();
      } else {
        onRowSubmit();
      }
    } else {
      alert("You should finish the guess first");
    }
  }

  //clear all the circles' colors and promp buttons when the "Play Again" button gets called
  useEffect(() => {
    setColors(Array(4).fill("#a3a3a3"));
    setMatch([null, null]);
  }, [isReset]);

  return (
    <>
      <div className="board-row">
        <div className="circle-container">
          <Circle color={colors[0]} onCircleClick={() => handleClick(0)} />
          <Circle color={colors[1]} onCircleClick={() => handleClick(1)} />
          <Circle color={colors[2]} onCircleClick={() => handleClick(2)} />
          <Circle color={colors[3]} onCircleClick={() => handleClick(3)} />
        </div>
        <div className="submit-container">
          {isActive() && (
            <button className="submit" onClick={handleSubmitClick}>
              Submit
            </button>
          )}
          {match[0] !== null && match[1] !== null && (
            <button className="submit">
              {match[0]} exact, {match[1]} partial
            </button>
          )}
        </div>
      </div>
    </>
  );
}

function Board() {
  const [currentColor, setCurrentColor] = useState("#a3a3a3");
  const colors = ["red", "blue", "green", "yellow", "orange", "purple"];
  function handleColorPick(color) {
    setCurrentColor(color);
  }
  let dislayColor;
  currentColor == "#a3a3a3"
    ? (dislayColor = "You haven't selected yet")
    : (dislayColor = currentColor);

  const [currentActiveRow, setCurrentActiveRow] = useState(9);
  function handleSubmit() {
    setCurrentActiveRow(currentActiveRow - 1);
    console.log(colorSequence);
  }

  const [colorSequence, setColorSequence] = useState([]);

  function generateSequence() {
    const sequence = [];
    for (let i = 0; i < 4; i++) {
      const randomIndex = Math.floor(Math.random() * colors.length);
      sequence.push(colors[randomIndex]);
    }
    setColorSequence(sequence);
    console.log(colorSequence);
  }

  useEffect(() => {
    generateSequence();
    console.log(colorSequence);
  }, []);

  const [isReset, setIsReset] = useState(false);

  function playAgain() {
    generateSequence();
    setCurrentColor(null);
    setIsReset(!isReset);
    setCurrentActiveRow(9);
  }

  const [isWin, setIsWin] = useState(false);

  function handleWin() {
    setIsWin(true);
  }

  let status;
  let instruction;
  if (isWin) {
    status = <p>YOU WIN!</p>;
    instruction = <Image src="smileface.jpeg" rounded />;
  } else if (currentActiveRow < 0) {
    status = <p>YOU LOST!</p>;
    instruction = <Image src="sadface.jpeg" rounded />;
  } else {
    status = <p>You have {currentActiveRow + 1} chances left.</p>;
    instruction = <Instruction />;
  }

  return (
    <div className="App">
      <MastermindHeader />
      <div className="board">
        <div className="row mt-3">
          <div className="col-md-4">
            {Array(10)
              .fill(null)
              .map((row, index) => {
                return (
                  <OneGuessBoard
                    key={index}
                    selectColor={currentColor}
                    isActive={() => index === currentActiveRow}
                    phrase={colorSequence}
                    onRowSubmit={handleSubmit}
                    isReset={isReset}
                    onWin={handleWin}
                  />
                );
              })}
          </div>

          <div className="col-md-3">
            <div className="instruction">{instruction}</div>
            <div className="prompt">{status}</div>
          </div>

          <div className="col-md-5">
            <div className="color-pick-section">
              <h2>Choose a Color:</h2>
              <div className="color-pick-box">
                {colors.map((color, index) => (
                  <button
                    key={index}
                    className="color-pick-button"
                    style={{ backgroundColor: color }}
                    onClick={() => handleColorPick(color)}
                  ></button>
                ))}
              </div>
              <p className="select">Selected Color: {dislayColor}</p>
            </div>
          </div>
        </div>

        <div className="play-again">
          <Button className="play-again-button" size="lg" onClick={playAgain}>
            Play Again
          </Button>
        </div>
      </div>
    </div>
  );
}

export default Board;
