import React from "react";
import { useState } from "react";

import Board from "./Pages/Board";
import StartPage from "./Pages/StartPage";
import GameRecord from "./Pages/GameRecord";
import LoginForm from "./Pages/LoginForm";

// Component of the entire game board, the level of a round of game
function App() {
  // user is the currently logged in user
  const [user, setUser] = useState(null);

  // this will be called by the LoginForm
  const HandleLogin = (user) => {
    setUser(user);
  };

  const [handle, setHandle] = useState("");
  const handleHandle = (name) => {
    setHandle(name);
  };

  const [inGame, setInGame] = useState(true);
  const handleInGame = () => {
    setInGame(!inGame);
  };

  return (
    <>
      <LoginForm LoginEvent={HandleLogin} />
      {handle ? (
        inGame ? (
          <Board
            onHandleChange={handleHandle}
            onGamePageChange={handleInGame}
          />
        ) : (
          <GameRecord onGamePageChange={handleInGame} />
        )
      ) : (
        <StartPage onHandleChange={handleHandle} />
      )}
    </>
  );
}

export default App;
