function CheckExacts(generatColors, guessColors) {
    i = 0;
    exact = 0;
    while (i < 4) {
        //When a match is made, cross out the matched elements in both guess and secret.
        if (phraseSB.charAt(i) == guessSB.charAt(i)) {
            exact = exact + 1;
            phraseSB.setCharAt(i, '-');
            guessSB.setCharAt(i, '*');
        }
        i++;
    }
    return exact;
}

function CheckPartials(generatColors, guessColors) {
    let i = 0;
    partial = 0;
    while (i < 4) {
        let j = 0;
        while (j < 4) {
        //When a match is made, cross out the matched elements in both guess and secret.
            if (phraseSB.charAt(i) == guessSB.charAt(j)) {
                partial = partial + 1;
                phraseSB.setCharAt(i,'-');
                guessSB.setCharAt(j,'*');
            }
        j++;
        }
            i++;
        }
    return partial;
}

function CheckMatches(generatColors, guessColors) {
    const exact = CheckExacts(generatColors, guessColors);
    const partial = CheckPartials(generatColors, guessColors);
    return [exact, partial];
}

export default CheckMatches;
