import { useState } from 'react';

function Circle() {
  const [buttoncolor, setButtonColor] = useState('white');
  const handleClick = () => {
    setButtonColor('red');
  };

  return (
    <button className="circle" onClick={handleClick} 
    style={{backgroundColor: buttoncolor}}>1</button>
  )
}

function OneGuessBoard() {
  return (
    <div className="board-row">
        <Circle />
        <Circle />
        <Circle />
        <Circle />
    </div>
  )
}

function Board() {
  const [currentColor, setCurrentColor] = useState(null);
  const colors = ['red', 'blue', 'green', 'yellow', 'orange', 'purple'];
  const handleColorPick = (color) => {
    setCurrentColor(color);
  };

  return (
    <>
    {Array(10).fill(null).map( (row) => {
      return (<OneGuessBoard/>);
    } )}
    <div>
      <h2>Choose a Color:</h2>
      <div className="color-pick-box">
        {colors.map((color, index) => (
          <button
            key={index}
            className="color-pick-button"
            style={{ backgroundColor: color }}
            onClick={() => handleColorPick(color)}
          >null</button>
        ))}
      </div>
      <p>Selected Color: {currentColor}</p>
    </div>
    </>
  )  
}

export default Board;
