import React, { useState, useEffect } from "react";
import { Row, Col, Pagination, Dropdown } from "react-bootstrap";

function PaginationComponent({
  itemsPerPage,
  totalItems,
  onPageChange,
  onItemsPerPageChange,
}) {
  const [currentPage, setCurrentPage] = useState(1);
  const [pages, setPages] = useState([]); //an array of int from 1
  const itemsPerPageOptions = [5, 10, 20];

  useEffect(() => {
    const totalPages = Math.ceil(totalItems / itemsPerPage);
    console.log("Items per page is " + itemsPerPage);
    console.log(totalPages + " pages in total");
    const newPages = Array.from(
      { length: totalPages },
      (_, index) => index + 1
    );
    setPages(newPages);
  }, [totalItems, itemsPerPage]);

  const handlePageChange = (page) => {
    setCurrentPage(page);
    onPageChange(page);
  };

  const handleItemsPerPageChange = (size) => {
    setCurrentPage(1); // Reset to the first page when changing items per page
    onItemsPerPageChange(size);
  };

  return (
    <Row className="justify-content-center mt-3">
      <Col md={4} className="d-flex justify-content-center align-items-center">
        <Dropdown>
          <Dropdown.Toggle variant="info" id="dropdown-basic">
            records per page: {itemsPerPage}
          </Dropdown.Toggle>
          <Dropdown.Menu>
            {itemsPerPageOptions.map((option) => (
              <Dropdown.Item
                key={option}
                onClick={() => handleItemsPerPageChange(option)}
              >
                {`${option} /page`}
              </Dropdown.Item>
            ))}
          </Dropdown.Menu>
        </Dropdown>
      </Col>
      <Col md={8} className="d-flex justify-content-center align-items-center">
        <Pagination>
          {pages.map((page) => (
            <Pagination.Item
              key={page}
              active={page === currentPage}
              onClick={() => handlePageChange(page)}
            >
              {page}
            </Pagination.Item>
          ))}
        </Pagination>
      </Col>
    </Row>
  );
}

export default PaginationComponent;
